mapper.insert:
INSERT INTO member (
email, passwordHash, memberName, birthDate, provider, createdAt, updatedAt
) VALUES (
#{email}, #{passwordHash}, #{memberName}, #{birthDate}, #{provider}, NOW(), NOW()
)

mapper.update:
UPDATE member SET memberName = #{memberName}, birthDate = #{birthDate}, updatedAt = NOW()
, passwordHash = #{passwordHash}
WHERE email = #{email}

UPDATE member
SET
updatedAt = NOW()

, province = #{province}
, city = #{city}

, gender = #{gender}
, birthDate = #{birthDate}

, personalIncome = #{personalIncome}
, familyIncome = #{familyIncome}
, familySize = #{familySize}

, eduLevelCode = #{eduLevelCode}
, empStatusCode = #{empStatusCode}

, married = #{married}
, child = #{child}
, home = #{home}

, businessRegistered = #{businessRegistered}

WHERE email = #{email}
