mapper.insert:
INSERT INTO member (
email, passwordHash, memberName, birthDate, createdAt, updatedAt
) VALUES (
#{email}, #{passwordHash}, #{memberName}, #{birthDate}, NOW(), NOW()
)

mapper.update:
UPDATE member SET memberName = #{memberName}, birthDate = #{birthDate}, updatedAt = NOW()
, passwordHash = #{passwordHash}
WHERE email = #{email}
